#include "authentication.hpp"

#include <chrono>
#include <string>

#include <jwt-cpp/jwt.h>

using namespace std;

string generateToken(const string& email)
{
	return jwt::create()
	           .set_issuer("Aperture Notepad")
	           .set_subject(email)
	           .set_expires_at(chrono::system_clock::now() + chrono::months(1))
	           .sign(jwt::algorithm::hs256(""));
}